import React from "react";
import { Menu, Input, Dropdown, Image } from "semantic-ui-react";
import { Link } from "../routes";
import COLORS from "../colors";

export default () => {

  return (
    <Menu borderless style={{ backgroundColor: COLORS.menuBackground }}>
      <Link route="/">
        <a className="logo">
          {/* <Image src={logo} size="small" alt="Logo"/> */}
          <strong>Ethegram</strong>
        </a>
      </Link>

      <Menu.Menu position="right">

        <Dropdown
          simple
          className="item"
          text="Menu"
          openOnFocus
          button
          style={{
            backgroundColor: COLORS.menuBackground,
            color: COLORS.menuText,
            fontSize: "20px"
          }}
        >
          <Dropdown.Menu style={{ borderColor: COLORS.menuBackground, borderWidth: "2px", backgroundColor: COLORS.ddbackground }}>
            <Dropdown.Item>
              <Link route="/profile">
                <a className="menu">Profile</a>
              </Link>
            </Dropdown.Item>
            <Dropdown.Item>
              <Link route="/chat">
                <a className="menu">Chat Room</a>
              </Link>
            </Dropdown.Item>
            <Dropdown.Item>
              <Link route="/users">
                <a className="menu">Users</a>
              </Link>
            </Dropdown.Item>
            <Dropdown.Divider style={{ borderColor: COLORS.menuText }} />
            <Dropdown.Item>
              <Link route="/about">
                <a className="menu">About</a>
              </Link>
            </Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      </Menu.Menu>
      <style jsx>{`
        .logo {
          font-family: "Arial";
          color: #fff;
          font-size: 30px;
          padding: 30px;
          padding-bottom: 30px;
        }

        .menu {
          font-size: 18px;
          color: #fff;
        }

        .logo:hover, .menu:hover {
          opacity: 0.6;
          font-size: 220%;
        }

      `}
      </style>
    </Menu>
  );
};
