import React, { Component } from "react";
import Layout from "../../../components/Layout";
import social from "../../../ethereum/social";
import { Container, Grid } from "semantic-ui-react";
import COLORS from "../../../colors";

class ViewUser extends Component {
  state = {
    userAddress: "",
    userName: "",
    tokens: 0,
    userPosts: 0,
    redeemTokens: 0,
  };

  static async getInitialProps(props) {
    return {
      userAddress: props.query.address,
    };
  }

  async componentDidMount() {
    const userAddress = this.props.userAddress;

    const userDetails = await social.methods.getUserDetails(userAddress).call();
    console.log(userDetails);

    this.setState({
      userName: userDetails[0],
      tokens: userDetails[1],
      userPosts: userDetails[2],
      redeemTokens: userDetails[3],
      userAddress: userAddress,
    });
  }

  render() {
    return (
      <Layout>
        <Container>
          <br></br>
          <center>
            <h1 style={{ color: COLORS.menuBackground, fontSize: "50px" }}>
              {this.state.userName}
            </h1>
            <div
              style={{
                backgroundColor: COLORS.divBackground,
                padding: "10px",
                width: "500px",
                borderRadius: "20px",
                border: "1px solid",
                borderColor: COLORS.menuBackground,
              }}
            >
              <p
                style={{
                  color: COLORS.menuBackground,
                  fontWeight: "bold",
                  fontSize: "15px",
                }}
              >
                {this.state.userAddress}
              </p>
            </div>
          </center>
          <br />
          <br />
          <hr />
          <br />
          <br />
          <Grid centered>
            <Grid.Column width="8">
              <div
                style={{
                  padding: "20px",
                  height: "100%",
                  borderRadius: "50px",
                  backgroundColor: COLORS.menuBackground,
                  color: COLORS.menuText,
                  border: "2px solid",
                  borderColor: COLORS.divBackground,
                }}
              >
                <center>
                  <h1 style={{ fontSize: "40px" }}>Total Posts</h1>
                  <br />
                  <br />
                  <CountUp
                    style={{
                      fontSize: "80px",
                      alignContent: "center",
                      justifyContent: "center",
                    }}
                    start={0}
                    end={this.state.userposts}
                    duration={5}
                  />
                </center>
              </div>
            </Grid.Column>
            <Grid.Column width="8">
              <div
                style={{
                  padding: "20px",
                  height: "100%",
                  borderRadius: "50px",
                  backgroundColor: COLORS.divBackground,
                  color: COLORS.menuBackground,
                  border: "2px solid",
                  borderColor: COLORS.menuBackground,
                }}
              >
                <center>
                  <h1 style={{ fontSize: "40px" }}>Total Tokens Earned</h1>
                  <br />
                  <br />
                  <CountUp
                    style={{
                      fontSize: "80px",
                      alignContent: "center",
                      justifyContent: "center",
                    }}
                    start={0}
                    end={this.state.tokens}
                    duration={5}
                  />
                  <h1>GTok</h1>
                </center>
              </div>
            </Grid.Column>
          </Grid>
        </Container>
      </Layout>
    );
  }
}

export default ViewUser;
